import sys

VERBOSE = 1

alex_string = ""

#-------------------------------------------------------------------
#-------------------------------------------------------------------
def parse(s):
    first = s[0]
    acc = 0

    # Scan through the string and add if pairs match.
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            acc += int(s[i])

    # Handle the end case.
    if s[0] == s[-1]:
        acc += int(s[0])

    return acc


#-------------------------------------------------------------------
#-------------------------------------------------------------------
def alex_captcha(s):
    csum = 0
    for i in range(len(s)):
        nextindex = (i+1) % len(s)
        if s[i] == s[nextindex]:
            csum += int(s[i], 10)
    return csum

#-------------------------------------------------------------------
#-------------------------------------------------------------------
def get_input():
    with open('my_input.txt','r') as f:
        my_string = f.read()
    return my_string


#-------------------------------------------------------------------
#-------------------------------------------------------------------
def main():
    my_string = get_input()

    print("Teststrings:")
    print(parse("1122"), "Should be 3")
    print(parse("1111"), "Should be 4")
    print(parse("1234"), "Should be 0")
    print(parse("91212129"), "Should be 9")
    print("")

    print("My string:")
    print(parse(my_string))
    print(alex_captcha(my_string))
    print("")

    print("Alex string:")
    print(parse(alex_string))
    print(alex_captcha(alex_string))
    print("")

#-------------------------------------------------------------------
#-------------------------------------------------------------------
if __name__=="__main__":
    # Run the main function.
    sys.exit(main())


